int Map::reverse_move(int move) {
  switch(move) {
    default:
      throw "Invalid move";
    case UP:
      return DOWN;
    case DOWN:
      return UP;
    case LEFT:
      return RIGHT;
    case RIGHT:
      return LEFT;
  };
  return current_location;
};

int Map::moves(int row, int col) {
    return map[row*_width + col];
}

int Map::move(int _move) {
  switch(map[current_location] & _move) {
    case STOP:
      throw "Invalid move";
    case UP:
      current_location -= _width;
      break;
    case DOWN:
      current_location += _width;
      break;
    case LEFT:
      current_location -= 1;
      break;
    case RIGHT:
      current_location += 1;
      break;
  };
  _moves++;
  return current_location;
};
